import React, { useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import Fab from "@mui/material/Fab";
import Zoom from "@mui/material/Zoom";
import LocalizationProvider from "@mui/lab/LocalizationProvider";
import DesktopDatePicker from "@mui/lab/DesktopDatePicker";
import DesktopTimePicker from "@mui/lab/DesktopTimePicker";
import AdapterDateFns from "@mui/lab/AdapterDateFns";

function CreateArea(props) {
  const [isExpanded, setExpanded] = useState(false);
  const [note, setNote] = useState({
    title: "",
    content: "",
    created: "", // Add a "created" property to store the creation date and time
  });

  function capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  function getCurrentDateTime() {
    const now = new Date();
    return now.toLocaleString(); // Format the current date and time as a string
  }

  function handleChange(event) {
    const { name, value } = event.target;
    const updatedValue =
      name === "title" || name === "content"
        ? capitalizeFirstLetter(value)
        : value;

    setNote((prevNote) => ({
      ...prevNote,
      [name]: updatedValue,
    }));
  }

  function handleDateChange(date) {
    setNote((prevNote) => ({
      ...prevNote,
      created: date.toLocaleString(), // Format the selected date as a string
    }));
  }

  function submitNote(event) {
    props.onAdd({
      ...note,
    });
    setNote({
      title: "",
      content: "",
      created: "",
    });
    event.preventDefault();
  }

  function expand() {
    setExpanded(true);
  }

  return (
    <div>
      <form className="create-note">
        {isExpanded && (
          <input
            name="title"
            onChange={handleChange}
            value={note.title}
            placeholder="Title"
          />
        )}

        <textarea
          name="content"
          onClick={expand}
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows={isExpanded ? 3 : 1}
        />
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <DesktopDatePicker
            label="Date"
            value={note.created}
            onChange={handleDateChange}
            renderInput={() => <div />} // Render an empty div to adjust layout
          />
          <DesktopTimePicker
            label="Time"
            value={note.created}
            onChange={handleDateChange}
            renderInput={() => <div />} // Render an empty div to adjust layout
          />
        </LocalizationProvider>
        <Zoom in={isExpanded}>
          <Fab onClick={submitNote}>
            <AddIcon />
          </Fab>
        </Zoom>
      </form>
    </div>
  );
}

export default CreateArea;
