import React, { useState } from "react";
import AddIcon from "@mui/icons-material/Add";
import Fab from "@mui/material/Fab";
import Zoom from "@mui/material/Zoom";
import IconButton from "@mui/material/IconButton";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import EventIcon from "@mui/icons-material/Event";
import Popover from "@mui/material/Popover";
import TextField from "@mui/material/TextField";

function CreateArea(props) {
  const [anchorDatePicker, setAnchorDatePicker] = useState(null);
  const [anchorTimePicker, setAnchorTimePicker] = useState(null);
  const [isExpanded, setExpanded] = useState(false);
  const [note, setNote] = useState({
    title: "",
    content: "",
    createdDate: new Date().toLocaleDateString(),
    createdTime: new Date().toLocaleTimeString([], {
      hour: "2-digit",
      minute: "2-digit",
    }),
  });

  function capitalizeFirstLetter(string) {
    return string.charAt(0).toUpperCase() + string.slice(1);
  }

  function handleChange(event) {
    const { name, value } = event.target;
    const updatedValue =
      name === "title" || name === "content"
        ? capitalizeFirstLetter(value)
        : value;

    setNote((prevNote) => ({
      ...prevNote,
      [name]: updatedValue,
    }));
  }

  function handleDateChange(event) {
    setNote((prevNote) => ({
      ...prevNote,
      createdDate: event.target.value,
    }));
  }

  function handleTimeChange(event) {
    setNote((prevNote) => ({
      ...prevNote,
      createdTime: event.target.value,
    }));
  }

  function submitNote(event) {
    const { title, content, createdDate, createdTime } = note;
    const createdDateTime = new Date(`${createdDate} ${createdTime}`);
    props.onAdd({
      title,
      content,
      created: createdDateTime.toLocaleString(),
    });
    setNote({
      title: "",
      content: "",
      createdDate: new Date().toLocaleDateString(),
      createdTime: new Date().toLocaleTimeString([], {
        hour: "2-digit",
        minute: "2-digit",
      }),
    });
    event.preventDefault();
  }

  function expand() {
    setExpanded(true);
  }
  const handleDatePickerOpen = (event) => {
    setAnchorDatePicker(event.currentTarget);
  };

  const handleTimePickerOpen = (event) => {
    setAnchorTimePicker(event.currentTarget);
  };

  const handleDatePickerClose = () => {
    setAnchorDatePicker(null);
  };

  const handleTimePickerClose = () => {
    setAnchorTimePicker(null);
  };

  return (
    <div>
      <form className="create-note">
        {isExpanded && (
          <input
            name="title"
            onChange={handleChange}
            value={note.title}
            placeholder="Title"
          />
        )}

        <textarea
          name="content"
          onClick={expand}
          onChange={handleChange}
          value={note.content}
          placeholder="Take a note..."
          rows={isExpanded ? 3 : 1}
        />
        <div className="datetime-pickers">
          <label>Date:</label>
          <TextField
            type="text"
            name="createdDate"
            value={note.createdDate}
            onFocus={handleDatePickerOpen}
            InputProps={{
              startAdornment: (
                <IconButton size="small" onClick={handleDatePickerOpen}>
                  <EventIcon />
                </IconButton>
              ),
            }}
          />
          <Popover
            open={Boolean(anchorDatePicker)}
            anchorEl={anchorDatePicker}
            onClose={handleDatePickerClose}
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "left",
            }}
            transformOrigin={{
              vertical: "top",
              horizontal: "left",
            }}
          >
            {/* Date Picker Component */}
          </Popover>
          <label>Time:</label>
          <TextField
            type="text"
            name="createdTime"
            value={note.createdTime}
            onFocus={handleTimePickerOpen}
            InputProps={{
              startAdornment: (
                <IconButton size="small" onClick={handleTimePickerOpen}>
                  <AccessTimeIcon />
                </IconButton>
              ),
            }}
          />
          <Popover
            open={Boolean(anchorTimePicker)}
            anchorEl={anchorTimePicker}
            onClose={handleTimePickerClose}
            anchorOrigin={{
              vertical: "bottom",
              horizontal: "left",
            }}
            transformOrigin={{
              vertical: "top",
              horizontal: "left",
            }}
          >
            {/* Time Picker Component */}
          </Popover>
        </div>
        <Zoom in={isExpanded}>
          <Fab onClick={submitNote}>
            <AddIcon />
          </Fab>
        </Zoom>
      </form>
    </div>
  );
}

export default CreateArea;
